mapper.select:
select count( nick )
from user
where nick like #{ nick };

select count( id )
from user
where id like #{ id };

select count(*)
from user
where id like #{id} and pwd like #{pwd}

select id, nick, profile
from user
where id like #{id}

select pwd
from user
where id like #{id}

select count( id )
from user
where id = #{ id };

select count(*)
from user
where id = #{id} and mail = #{mail}

select id, mail, pwd
from user
where id = #{id} and mail = #{mail}

select pwd
from user
where id like #{id} and mail = #{mail}

select no, subject, writer, date_format(wdate, '%Y-%m-%d') wdate, content, hit, location, video, reply, heart
from l_board
where no=#{no}

mapper.update:
update l_reply
set grp = #{ grp }
where no = #{ no }

mapper.select:
select grp, grps, grpl
from l_reply
where no = #{ no }

mapper.update:
update l_reply
set grps = grps + 1
where grp = #{ grp } and grps > #{ grpl }

update l_reply
set content = #{ content } 
where no = #{ no }

